"use strict";

const insertElement = document.getElementById("values");
// Function that generates the number between two given numbers and stores it in array
function generateRandomNumbers(start, end) {
  const countNum = end - start;
  const result = [];

  while (result.length <= countNum) {
    const randomNumber = Math.floor(Math.random() * (end - start + 1)) + start;

    if (!result.includes(randomNumber)) {
      result.push(randomNumber);
    }
  }
  return result;
}
// This function insert numbers that have been generated by generateRandomNumbers
const insertValue = () => {
  const initialValue = parseInt(document.getElementById("initial").value);
  const finalValue = parseInt(document.getElementById("final").value);
  if (finalValue > initialValue) {
    const randomNumbers = generateRandomNumbers(initialValue, finalValue);
    insertElement.innerHTML = randomNumbers;
    console.log(initialValue);
    console.log(finalValue);
  } else {
    insertElement.innerHTML = "Initial value must be lower than Final value.";
  }
  console.log(initialValue + " " + finalValue);
};
// This function reset the values that are present inside the form
const resetHandler = () => {
  insertElement.innerHTML = "";
};
